/* Code from https://www.youtube.com/watch?v=Xcet6msf3eE&t=237s
 * Web Dev Simplified
 * "How to Build a Better Spotify with React"
 * 
 * Main menu button implemented by Matthew Greenway
 */
import React from 'react';
import { useState } from 'react'
//client id = 2387a2a4c594439a9f66f97fb886ca59
const AUTH_URL = "https://accounts.spotify.com/authorize?client_id=2387a2a4c594439a9f66f97fb886ca59&response_type=code&redirect_uri=http://localhost:3000&scope=streaming%20user-read-email%20user-read-private%20user-library-read%20user-library-modify%20user-read-playback-state%20user-modify-playback-state"

export default function Login() {
    const [chooseMainMenu, setChooseMainMenu] = useState(false)

    function handleMainMenu() {
        setChooseMainMenu(true)
    }

    if(!chooseMainMenu) {
        return ( <button style={{ width: 420, backgroundColor: '#F56600', marginTop: 20, minHeight: 50, }}>
        <h2 >Generate Workout</h2>
      </button>)
    }
    else {
        return ( <div>Main Menu Placeholder</div> )
    }
}

